mapper.select:
SELECT COUNT(*) > 0
FROM user
WHERE loginId = #{loginId}

mapper.update:
UPDATE user set username = #{username},
                email = #{email},
                role = #{role},
                oauthId = #{oauthId}
             where oauthId = #{oauthId}

UPDATE user SET username = #{username},
                email = #{email},
                birth = #{birth},
                gender = #{gender}
WHERE id = #{id}
