mapper.select:
SELECT COUNT(*) > 0
FROM user
WHERE loginId = #{loginId}

SELECT COUNT(*) > 0
FROM user
WHERE email = #{email}

SELECT COUNT(*) > 0
FROM user
WHERE username = #{username}

mapper.update:
UPDATE user set username = #{username},
                email = #{email},
                role = #{role},
                oauthId = #{oauthId}
             where oauthId = #{oauthId}

UPDATE user SET username = #{username},
                birth = #{birth},
                gender = #{gender},
                profile_image = #{profileImage}
WHERE id = #{id}
